import React, { Component } from "react";
import axios from "axios";
import verified from "../../verified.png";

const BASE_URL = "https://loud-final.herokuapp.com/get_more_users";

class Authors extends Component {
  state = {
    data: {},
    authorData: [],
  };
  componentDidMount() {
    axios
      .get(`${BASE_URL}`)
      .then((res) => {
        this.setState({
          data: res.data,
          authorData: res.data.topUsers,
        });
        // console.log(this.state.data)
      })
      .catch((err) => console.log(err));
  }
  render() {
    return (
      <section className="popular-collections-area">
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-12 col-md-8 col-lg-7">
              {/* Intro */}
              <div className="intro text-center">
                <span>{"Creators"}</span>
                <h3 className="mt-3 mb-0">{"Our Artists"}</h3>
                <p>{this.state.data.content}</p>
              </div>
            </div>
          </div>
          <div className="row items">
            {this.state.authorData.map((item, idx) => {
              return (
                <div
                  key={`ad_${idx}`}
                  className="col-12 col-sm-6 col-lg-3 item"
                >
                  <div className="card no-hover text-center">
                    <div className="image-over">
                      <a href={`/artist/${item.wallet_address}`}>
                        <img
                          className="card-img-top"
                          src={
                            item.profile_image !== ""
                              ? item.profile_image
                              : "data:image/jpeg;base64,"
                          }
                          alt=""
                        />
                      </a>
                    </div>
                    {/* Card Caption */}
                    <div className="card-caption col-12 p-0">
                      {/* Card Body */}
                      <div className="card-body mt-4 ">
                        <div>{"@" + item.profile_name}</div>
                        <div
                          style={{ display: "flex", justifyContent: "center" }}
                        >
                          <a href={`/artist/${item.wallet_address}`}>
                            <h5>{item.wallet_address.slice(0, 6) + "..."}</h5>
                          </a>
                          <div>
                            {item.is_verified ? (
                              <div>
                                <img
                                  src={verified}
                                  alt="no"
                                  style={{ width: "15px", height: "15px" }}
                                />
                              </div>
                            ) : (
                              ""
                            )}
                          </div>
                        </div>

                        <a
                          className="btn btn-bordered-white btn-smaller"
                          href={`/artist/${item.wallet_address}`}
                        >
                          {"View Profile"}
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </section>
    );
  }
}

export default Authors;
