import React, { Component } from "react";
import axios from "axios";

const BASE_URL =
  "https://my-json-server.typicode.com/themeland/netstorm-json/collections";

class Collections extends Component {
  state = {
    data: {},
    collectionData: [],
    loading: true,
  };
  componentDidMount() {
    axios
      .get(`https://loud-final.herokuapp.com/get_collectibles`)
      .then((res) => {
        console.log(res.data);
        this.setState({
          data: res.data.collectible,
          collectionData: res.data.collectible,
          loading: false,
        });
        // console.log(this.state.data)
      })
      .catch((err) => console.log(err));
  }
  render() {
    if (this.state.loading) {
      return (
        <div style={{ height: "80vh" }}>
          <center>
            <div class="fa-3x mt-5 pt-5">
              <i class="fas fa-spinner fa-spin"></i>
            </div>
          </center>
        </div>
      );
    } else {
      return (
        <section className="popular-collections-area">
          <div className="container">
            <div className="row">
              <div className="col-12">
                {/* Intro */}
                <div className="intro d-flex justify-content-between align-items-end m-0">
                  <div className="intro-content">
                    <span>{"Collectibles"}</span>
                    <h3 className="mt-3 mb-0">{"Popular"}</h3>
                  </div>
                  <div className="intro-btn">
                    <a className="btn content-btn text-left" href="/explore-2">
                      {"View All"}
                    </a>
                  </div>
                </div>
              </div>
            </div>
            <div className="row items">
              {this.state.collectionData.map((item, idx) => {
                return (
                  <a
                    key={`cd_${idx}`}
                    className="col-12 col-sm-6 col-lg-3 item"
                    href={`/collectibles/${item.token_id}`}
                  >
                    <div className="card no-hover text-center">
                      <div className="image-over">
                        <a href={`/collectibles/${item.token_id}`}>
                          <img
                            className="card-img-top"
                            src={item.cover_image}
                            alt=""
                          />
                        </a>
                        {/* Seller */}
                        {/* <a className="seller" href="/item-details">
                          <div className="seller-thumb avatar-lg">
                            <img
                              className="rounded-circle"
                              src={
                                item.creator_image === ""
                                  ? "data:image/jpeg;base64,"
                                  : item.creator_image
                              }
                              alt=""
                            />
                          </div>
                        </a> */}
                      </div>
                      {/* Card Caption */}
                      <div className="card-caption col-12 p-0">
                        {/* Card Body */}
                        <div className="card-body mt-4">
                          <a href={`/collectibles/${item.token_id}`}>
                            <h5 className="mb-2">{item.name}</h5>
                          </a>
                          <span>{"Quantity: " + item.quantity}</span>
                          <div>Item Price: {item.item_price + " BNB "}ðŸ”¥</div>
                        </div>
                      </div>
                    </div>
                  </a>
                );
              })}
            </div>
          </div>
        </section>
      );
    }
  }
}

export default Collections;
